import{Emitter}from"../common/event.js";export class DomEmitter{get event(){return this.emitter.event}constructor(e,t,r){const i=e=>this.emitter.fire(e);this.emitter=new Emitter({onFirstListenerAdd:()=>e.addEventListener(t,i,r),onLastListenerRemove:()=>e.removeEventListener(t,i,r)})}dispose(){this.emitter.dispose()}}