import{StopWatch}from"../../../../base/common/stopwatch.js";import{EditorAction,registerEditorAction}from"../../../browser/editorExtensions.js";import*as nls from"../../../../nls.js";class ForceRetokenizeAction extends EditorAction{constructor(){super({id:"editor.action.forceRetokenize",label:nls.localize("forceRetokenize","Developer: Force Retokenize"),alias:"Developer: Force Retokenize",precondition:void 0})}run(o,e){if(!e.hasModel())return;const t=e.getModel();t.tokenization.resetTokenization();const i=new StopWatch(!0);t.tokenization.forceTokenization(t.getLineCount()),i.stop(),console.log(`tokenization took ${i.elapsed()}`)}}registerEditorAction(ForceRetokenizeAction);