import{Disposable}from"../../../base/common/lifecycle.js";import{MultiplexLogger}from"./log.js";export class LogService extends Disposable{constructor(e,g=[]){super(),this.logger=new MultiplexLogger([e,...g]),this._register(e.onDidChangeLogLevel((e=>this.setLevel(e))))}get onDidChangeLogLevel(){return this.logger.onDidChangeLogLevel}setLevel(e){this.logger.setLevel(e)}getLevel(){return this.logger.getLevel()}trace(e,...g){this.logger.trace(e,...g)}debug(e,...g){this.logger.debug(e,...g)}info(e,...g){this.logger.info(e,...g)}error(e,...g){this.logger.error(e,...g)}}